import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useSound } from 'use-sound';

const InteractiveTemplate = ({ template, surprise, onComplete }) => {
  const [currentStep, setCurrentStep] = useState(0);
  const [stepState, setStepState] = useState({});
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);

  // Sound effects (using use-sound hook)
  const [playDoorCreak] = useSound('/sounds/door-creak.mp3', { volume: 0.5 });
  const [playBalloonPop] = useSound('/sounds/balloon-pop.mp3', { volume: 0.3 });
  const [playBirthdayMusic] = useSound('/sounds/birthday-music.mp3', { volume: 0.4 });
  const [playCelebration] = useSound('/sounds/celebration.mp3', { volume: 0.5 });

  const currentStepConfig = template.config.steps[currentStep];

  useEffect(() => {
    // Auto-advance for auto-action steps
    if (currentStepConfig?.action === 'auto') {
      const timer = setTimeout(() => {
        handleStepComplete();
      }, currentStepConfig.duration || 3000);
      
      return () => clearTimeout(timer);
    }
  }, [currentStep, currentStepConfig]);

  const handleStepComplete = () => {
    if (currentStep < template.config.steps.length - 1) {
      setCurrentStep(currentStep + 1);
      setStepState({});
    } else {
      onComplete && onComplete();
    }
  };

  const handleInteraction = (stepId, data = {}) => {
    setStepState(prev => ({ ...prev, [stepId]: data }));
    
    // Play appropriate sound
    switch (stepId) {
      case 'door':
        playDoorCreak();
        break;
      case 'balloons':
        playBalloonPop();
        break;
      case 'lights':
        playBirthdayMusic();
        break;
      case 'final':
        playCelebration();
        break;
      default:
        break;
    }

    // Auto-advance if specified
    if (currentStepConfig?.next) {
      setTimeout(() => {
        handleStepComplete();
      }, 1000);
    }
  };

  const renderStep = () => {
    switch (currentStepConfig?.scene) {
      case 'DarkRoom':
        return <DarkRoomScene step={currentStepConfig} onInteraction={handleInteraction} />;
      case 'LightReveal':
        return <LightRevealScene step={currentStepConfig} onInteraction={handleInteraction} />;
      case 'BalloonInteraction':
        return <BalloonInteractionScene step={currentStepConfig} onInteraction={handleInteraction} />;
      case 'FinalReveal':
        return <FinalRevealScene step={currentStepConfig} onInteraction={handleInteraction} />;
      default:
        return <div>Loading...</div>;
    }
  };

  return (
    <div className="relative w-full h-screen overflow-hidden bg-black">
      {/* Background Audio */}
      <audio ref={audioRef} preload="auto" />
      
      {/* Progress Bar */}
      <div className="absolute top-4 left-4 right-4 z-50">
        <div className="bg-gray-800 rounded-full h-2">
          <motion.div
            className="bg-gradient-to-r from-yellow-400 to-orange-500 h-2 rounded-full"
            initial={{ width: 0 }}
            animate={{ width: `${((currentStep + 1) / template.config.steps.length) * 100}%` }}
            transition={{ duration: 0.5 }}
          />
        </div>
        <div className="text-white text-sm mt-2 text-center">
          Step {currentStep + 1} of {template.config.steps.length}
        </div>
      </div>

      {/* Main Content */}
      <AnimatePresence mode="wait">
        <motion.div
          key={currentStep}
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          exit={{ opacity: 0, scale: 1.2 }}
          transition={{ duration: 0.5 }}
          className="w-full h-full"
        >
          {renderStep()}
        </motion.div>
      </AnimatePresence>

      {/* Skip Button */}
      <button
        onClick={onComplete}
        className="absolute bottom-4 right-4 z-50 bg-gray-800 text-white px-4 py-2 rounded-lg hover:bg-gray-700 transition-colors"
      >
        Skip to End
      </button>
    </div>
  );
};

// Dark Room Scene with Golden Door
const DarkRoomScene = ({ step, onInteraction }) => {
  const [doorClicked, setDoorClicked] = useState(false);

  const handleDoorClick = () => {
    setDoorClicked(true);
    onInteraction('door', { opened: true });
  };

  return (
    <div className="relative w-full h-full bg-gradient-to-b from-gray-900 to-black flex items-center justify-center">
      {/* Ambient particles */}
      <div className="absolute inset-0">
        {[...Array(20)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute w-1 h-1 bg-yellow-400 rounded-full"
            animate={{
              x: [0, 100, 0],
              y: [0, -100, 0],
              opacity: [0, 1, 0],
            }}
            transition={{
              duration: 3 + Math.random() * 2,
              repeat: Infinity,
              delay: Math.random() * 2,
            }}
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
          />
        ))}
      </div>

      {/* Golden Door */}
      <motion.div
        className="relative cursor-pointer"
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        onClick={handleDoorClick}
      >
        <motion.div
          className="w-64 h-96 bg-gradient-to-b from-yellow-400 via-yellow-500 to-yellow-600 rounded-lg shadow-2xl border-4 border-yellow-300"
          animate={doorClicked ? { rotateY: 90 } : { rotateY: 0 }}
          transition={{ duration: 2, ease: "easeInOut" }}
          style={{ transformStyle: "preserve-3d" }}
        >
          {/* Door handle */}
          <div className="absolute right-4 top-1/2 transform -translate-y-1/2">
            <div className="w-6 h-6 bg-yellow-700 rounded-full shadow-lg" />
          </div>
          
          {/* Door panels */}
          <div className="absolute inset-4 border-2 border-yellow-300 rounded" />
          <div className="absolute top-8 left-8 right-8 bottom-8 border border-yellow-200 rounded" />
        </motion.div>
      </motion.div>

      {/* Click instruction */}
      {!doorClicked && (
        <motion.div
          className="absolute bottom-20 text-yellow-400 text-xl font-semibold"
          animate={{ opacity: [0.5, 1, 0.5] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          Click the golden door to enter...
        </motion.div>
      )}
    </div>
  );
};

// Light Reveal Scene
const LightRevealScene = ({ step, onInteraction }) => {
  const [lightsOn, setLightsOn] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setLightsOn(true);
      onInteraction('lights', { illuminated: true });
    }, 1000);
    
    return () => clearTimeout(timer);
  }, []);

  return (
    <motion.div
      className="relative w-full h-full flex items-center justify-center"
      animate={{
        backgroundColor: lightsOn ? '#FFE5E5' : '#1a1a1a',
      }}
      transition={{ duration: 2 }}
    >
      {/* Room decorations */}
      <AnimatePresence>
        {lightsOn && (
          <>
            {/* Balloons */}
            {[...Array(8)].map((_, i) => (
              <motion.div
                key={i}
                className="absolute w-16 h-20 rounded-full"
                style={{
                  backgroundColor: ['#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FFEAA7'][i % 5],
                  left: `${20 + (i * 10)}%`,
                  top: `${30 + (i % 2 * 20)}%`,
                }}
                initial={{ scale: 0, y: 100 }}
                animate={{ scale: 1, y: 0 }}
                transition={{ delay: i * 0.2, duration: 0.8 }}
              />
            ))}

            {/* Confetti */}
            {[...Array(50)].map((_, i) => (
              <motion.div
                key={i}
                className="absolute w-2 h-2 rounded-full"
                style={{
                  backgroundColor: ['#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FFEAA7'][i % 5],
                  left: `${Math.random() * 100}%`,
                  top: `${Math.random() * 100}%`,
                }}
                initial={{ y: -20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: i * 0.1, duration: 1 }}
              />
            ))}

            {/* Welcome message */}
            <motion.div
              className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-center"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 1, duration: 0.8 }}
            >
              <h1 className="text-4xl font-bold text-pink-600 mb-4">
                üéâ Welcome to Your Birthday Surprise! üéÇ
              </h1>
              <p className="text-xl text-gray-700">
                The magic is about to begin...
              </p>
            </motion.div>
          </>
        )}
      </AnimatePresence>
    </motion.div>
  );
};

// Balloon Interaction Scene
const BalloonInteractionScene = ({ step, onInteraction }) => {
  const [poppedBalloons, setPoppedBalloons] = useState(new Set());
  const [revealedContent, setRevealedContent] = useState([]);
  
  const totalBalloons = step.count || 5;
  const allPopped = poppedBalloons.size === totalBalloons;

  const handleBalloonPop = (balloonId) => {
    if (poppedBalloons.has(balloonId)) return;
    
    setPoppedBalloons(prev => new Set([...prev, balloonId]));
    
    // Add revealed content
    const newContent = {
      id: balloonId,
      type: 'message',
      content: `Birthday wish ${balloonId + 1} üéà`,
      timestamp: Date.now()
    };
    setRevealedContent(prev => [...prev, newContent]);
    
    onInteraction('balloons', { popped: balloonId, total: totalBalloons });
    
    // Check if all balloons are popped
    if (poppedBalloons.size + 1 === totalBalloons) {
      setTimeout(() => {
        onInteraction('balloons', { completed: true });
      }, 1000);
    }
  };

  return (
    <div className="relative w-full h-full bg-gradient-to-b from-pink-100 to-purple-100 flex items-center justify-center">
      {/* Floating balloons */}
      {[...Array(totalBalloons)].map((_, i) => (
        <motion.div
          key={i}
          className={`absolute cursor-pointer ${poppedBalloons.has(i) ? 'pointer-events-none' : ''}`}
          style={{
            left: `${15 + (i * 15)}%`,
            top: `${20 + (i % 2 * 30)}%`,
          }}
          animate={poppedBalloons.has(i) ? {} : {
            y: [0, -20, 0],
            rotate: [0, 5, -5, 0],
          }}
          transition={{ duration: 3, repeat: Infinity, delay: i * 0.3 }}
        >
          <motion.div
            className={`w-20 h-24 rounded-full shadow-lg ${
              poppedBalloons.has(i) ? 'opacity-0 scale-0' : 'opacity-100 scale-100'
            }`}
            style={{
              backgroundColor: ['#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FFEAA7'][i % 5],
            }}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            onClick={() => handleBalloonPop(i)}
            transition={{ duration: 0.3 }}
          >
            {/* Balloon string */}
            <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-0.5 h-16 bg-gray-400" />
          </motion.div>
        </motion.div>
      ))}

      {/* Revealed content */}
      <div className="absolute bottom-20 left-4 right-4">
        <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
          {revealedContent.map((content) => (
            <motion.div
              key={content.id}
              className="bg-white p-4 rounded-lg shadow-lg border-2 border-pink-200"
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5 }}
            >
              <p className="text-center text-gray-700 font-semibold">
                {content.content}
              </p>
            </motion.div>
          ))}
        </div>
      </div>

      {/* Progress indicator */}
      <div className="absolute top-8 left-1/2 transform -translate-x-1/2">
        <div className="bg-white p-4 rounded-lg shadow-lg">
          <p className="text-lg font-semibold text-gray-700">
            üéà {poppedBalloons.size} of {totalBalloons} balloons popped
          </p>
        </div>
      </div>

      {/* Completion message */}
      {allPopped && (
        <motion.div
          className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2"
          initial={{ opacity: 0, scale: 0.5 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.8 }}
        >
          <div className="bg-gradient-to-r from-pink-400 to-purple-500 text-white p-8 rounded-2xl shadow-2xl text-center">
            <h2 className="text-3xl font-bold mb-4">üéâ All Balloons Popped! üéà</h2>
            <p className="text-xl">Ready for the final surprise?</p>
          </div>
        </motion.div>
      )}
    </div>
  );
};

// Final Reveal Scene
const FinalRevealScene = ({ step, onInteraction }) => {
  const [showFinale, setShowFinale] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowFinale(true);
      onInteraction('final', { revealed: true });
    }, 1000);
    
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="relative w-full h-full bg-gradient-to-b from-purple-900 via-pink-800 to-red-600 flex items-center justify-center">
      {/* Fireworks effect */}
      {showFinale && (
        <div className="absolute inset-0">
          {[...Array(30)].map((_, i) => (
            <motion.div
              key={i}
              className="absolute w-2 h-2 rounded-full"
              style={{
                backgroundColor: ['#FFD700', '#FF6B6B', '#4ECDC4', '#45B7D1', '#FFEAA7'][i % 5],
                left: `${Math.random() * 100}%`,
                top: `${Math.random() * 100}%`,
              }}
              animate={{
                scale: [0, 2, 0],
                opacity: [0, 1, 0],
                y: [0, -100, -200],
                x: [0, Math.random() * 100 - 50, Math.random() * 100 - 50],
              }}
              transition={{
                duration: 2 + Math.random() * 2,
                repeat: Infinity,
                delay: Math.random() * 2,
              }}
            />
          ))}
        </div>
      )}

      {/* Main celebration content */}
      <AnimatePresence>
        {showFinale && (
          <motion.div
            className="text-center text-white z-10"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1 }}
          >
            <motion.h1
              className="text-6xl md:text-8xl font-bold mb-8"
              animate={{ scale: [1, 1.1, 1] }}
              transition={{ duration: 2, repeat: Infinity }}
            >
              üéâ Happy Birthday! üéÇ
            </motion.h1>
            
            <motion.p
              className="text-2xl md:text-3xl mb-8"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.5, duration: 1 }}
            >
              May your day be filled with joy, laughter, and wonderful surprises! ‚ú®
            </motion.p>

            {/* Celebration buttons */}
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <motion.button
                className="bg-white text-purple-600 px-8 py-4 rounded-full text-xl font-semibold hover:bg-gray-100 transition-colors"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => window.location.reload()}
              >
                üéÅ Replay Surprise
              </motion.button>
              
              <motion.button
                className="bg-gradient-to-r from-pink-400 to-purple-500 text-white px-8 py-4 rounded-full text-xl font-semibold hover:from-pink-500 hover:to-purple-600 transition-all"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => {
                  // Share functionality
                  if (navigator.share) {
                    navigator.share({
                      title: 'Birthday Surprise!',
                      text: 'Check out this amazing birthday surprise!',
                      url: window.location.href
                    });
                  }
                }}
              >
                üì§ Share Joy
              </motion.button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default InteractiveTemplate;
